mapper.update:
UPDATE board 
SET b_title=#{b_title}, b_content=#{b_content}, b_type=#{b_type}, b_img=#{b_img}
WHERE b_no=#{b_no};

mapper.select:
SELECT * FROM board
ORDER BY b_type, b_regdate DESC 
LIMIT #{paginationInfo.firstRecordIndex}, #{recordsPerPage};

mapper.update:
UPDATE board
SET b_view = b_view + 1
WHERE b_no = #{b_no};
