mapper.select:
select * from mission
where m_date >= CURRENT_DATE() and m_type='group'
order by m_code;

select * from mission
where  CURRENT_DATE() > m_date and m_type='group'
order by m_code;

select * from mission
where m_date >= CURRENT_DATE() and m_type='solo'
order by m_code;

select * from mission
where  CURRENT_DATE() > m_date and m_type='solo'
order by m_code;
