mapper.select:
  select  p_code, p.id as id, s.m_code as m_code, m_name, m_photo, p_friends, m_date, p_complete, p_review, p_star, m_carbon, m_point, m_personnel, point, m_inform, carbon
	 from member m, participation p, mission s
   where p.id =m.id and s.m_code = p.m_code and p_code = #{p_code};

mapper.update:
update participation 
set p_complete = 1, p_photo = 'seawaste.jpg' 
where p_code = #{p_code};


 update member
set point = point + #{m_point} , carbon = carbon + #{m_carbon}
where id = #{id};


 update mission
set m_personnel = m_personnel - #{p_friends}
where m_code = #{m_code};
